                0% {
                    opacity: 0;
                    transform: scale(0.2) translate(0, 0)
                }

                5% {
                    opacity: 1;
                    transform: scale(0.3) translate(+5, -10px)
                }

                60% {
                    opacity: 1;

                }

                100% {
                    opacity: 0;
                    transform: scale(1.3) translate(+12px, -10px)
                }
            }

            #smoke_2 {
                animation: smokeR 2s 1s infinite
            }
            #Phase_color{
                animation: scale 2s infinite;
                transform-origin: 50% 15%;

            }

            @keyframes scale {
                0% {
                  transform: scale(1);

                }
             
                50% {
                    transform: scale(1.2);

                }

                100% {
                  transform: scale(1);

                }
             
